import React from 'react';
import { BrowserRouter as Router, Route, Switch } from 'react-router-dom';
import Builder from './components/Builder';  // Ensure this path is correct
import Cart from './components/Cart';        // Ensure this path is correct


const App = () => {
  const [cartItems, setCartItems] = useState([]);

  // Add selected components to the cart
  const addToCart = (selectedComponents) => {
    const totalPrice = Object.values(selectedComponents).reduce(
      (total, component) => total + (component ? component.price : 0),
      0
    );
    const cartItem = { ...selectedComponents, totalPrice };
    setCartItems([...cartItems, cartItem]); // Add to cart
  };

  const removeFromCart = (index) => {
    setCartItems(cartItems.filter((_, i) => i !== index)); // Remove item from cart
  };

  const clearCart = () => {
    setCartItems([]); // Clear all items in cart
  };

  return (
    <Router>
      <Switch>
        <Route exact path="/">
          <Builder addToCart={addToCart} />
        </Route>
        <Route path="/cart">
          <Cart cartItems={cartItems} removeFromCart={removeFromCart} clearCart={clearCart} />
        </Route>
      </Switch>
    </Router>
  );
};

export default App;
