import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Switch } from 'react-router-dom';
import Builder from './pages/Builder'; // Builder Component
import Cart from './pages/Cart'; // Cart Component
import Checkout from './pages/Checkout'; // Checkout Component

const App = () => {
  const [cartItems, setCartItems] = useState([]);

  const addToCart = (selectedComponents) => {
    const totalPrice = Object.values(selectedComponents).reduce((total, component) => {
      return total + (component ? component.price : 0);
    }, 0);

    setCartItems((prevCartItems) => [
      ...prevCartItems,
      { ...selectedComponents, totalPrice },
    ]);
  };

  const removeFromCart = (index) => {
    setCartItems(cartItems.filter((_, idx) => idx !== index));
  };

  const clearCart = () => {
    setCartItems([]);
  };

  return (
    <Router>
      <Switch>
        {/* Route for Builder */}
        <Route path="/builder">
          <Builder addToCart={addToCart} />
        </Route>
        
        {/* Route for Cart */}
        <Route path="/cart">
          <Cart cartItems={cartItems} removeFromCart={removeFromCart} clearCart={clearCart} />
        </Route>

        {/* Route for Checkout */}
        <Route path="/checkout">
          <Checkout cartItems={cartItems} />
        </Route>

        {/* Default Route (Home or any other page) */}
        <Route path="/" exact>
          <h1>Welcome to the PC Builder</h1>
          <p><a href="/builder">Go to Builder</a></p>
        </Route>
      </Switch>
    </Router>
  );
};

export default App;
