import React from 'react';
import { Link } from 'react-router-dom';

const Cart = ({ cartItems, removeFromCart, clearCart }) => {
  const totalPrice = cartItems.reduce((total, item) => {
    return total + item.totalPrice;
  }, 0);

  return (
    <div className="cart">
      <h1>Your Cart</h1>

      {cartItems.length === 0 ? (
        <p>Your cart is empty.</p>
      ) : (
        <>
          <div>
            {cartItems.map((item, index) => (
              <div key={index} className="cart-item">
                <h4>Custom PC {index + 1}</h4>
                {Object.keys(item).map((category) => (
                  category !== 'totalPrice' && item[category] && (
                    <p key={category}><strong>{category.charAt(0).toUpperCase() + category.slice(1)}:</strong> {item[category].name} - ${item[category].price}</p>
                  )
                ))}
                <p><strong>Total Price:</strong> ${item.totalPrice}</p>
                <button onClick={() => removeFromCart(index)}>Remove</button>
              </div>
            ))}
          </div>

          <div className="cart-total">
            <h3>Total Price: ${totalPrice}</h3>
            <button onClick={clearCart}>Clear Cart</button>
            <Link to="/checkout">
              <button>Proceed to Checkout</button>
            </Link>
          </div>
        </>
      )}
    </div>
  );
};

export default Cart;
