import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';

const Builder = () => {
  const [components, setComponents] = useState({ cpu: [], gpu: [], ram: [], storage: [] });
  const [cart, setCart] = useState([]);

  useEffect(() => {
    // Fetch available components from the backend
    axios.get('http://localhost:5000/api/components')
      .then(response => setComponents(response.data))
      .catch(error => console.log(error));
  }, []);

  const addToCart = (component) => {
    setCart([...cart, component]);
  };

  return (
    <div className="container mx-auto py-10">
      <h1 className="text-4xl font-bold text-center">Build Your PC</h1>

      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4 mt-8">
        {/* CPU Section */}
        <div>
          <h2 className="text-xl font-semibold">Choose CPU</h2>
          <ul>
            {components.cpu.map((cpu) => (
              <li key={cpu._id} className="border p-2 mb-2 cursor-pointer" onClick={() => addToCart(cpu)}>
                {cpu.name} - ${cpu.price}
              </li>
            ))}
          </ul>
        </div>

        {/* GPU Section */}
        <div>
          <h2 className="text-xl font-semibold">Choose GPU</h2>
          <ul>
            {components.gpu.map((gpu) => (
              <li key={gpu._id} className="border p-2 mb-2 cursor-pointer" onClick={() => addToCart(gpu)}>
                {gpu.name} - ${gpu.price}
              </li>
            ))}
          </ul>
        </div>

        {/* RAM Section */}
        <div>
          <h2 className="text-xl font-semibold">Choose RAM</h2>
          <ul>
            {components.ram.map((ram) => (
              <li key={ram._id} className="border p-2 mb-2 cursor-pointer" onClick={() => addToCart(ram)}>
                {ram.name} - ${ram.price}
              </li>
            ))}
          </ul>
        </div>

        {/* Storage Section */}
        <div>
          <h2 className="text-xl font-semibold">Choose Storage</h2>
          <ul>
            {components.storage.map((storage) => (
              <li key={storage._id} className="border p-2 mb-2 cursor-pointer" onClick={() => addToCart(storage)}>
                {storage.name} - ${storage.price}
              </li>
            ))}
          </ul>
        </div>
      </div>

      <div className="mt-6">
        <h2 className="text-2xl">Your Cart</h2>
        <ul>
          {cart.map((item, index) => (
            <li key={index}>{item.name} - ${item.price}</li>
          ))}
        </ul>
        <Link to="/checkout">
          <button className="bg-green-600 text-white py-3 px-6 mt-6 rounded">Proceed to Checkout</button>
        </Link>
      </div>
    </div>
  );
};

export default Builder;
