import React from 'react';
import { Link } from 'react-router-dom';

const Cart = ({ cartItems, removeFromCart, clearCart }) => {
  const totalPrice = cartItems.reduce((total, item) => {
    return total + item.totalPrice;
  }, 0);

  return (
    <div className="cart">
      <h1>Your Cart</h1>

      {cartItems.length === 0 ? (
        <p>Your cart is empty.</p>
      ) : (
        <>
          <div>
            {cartItems.map((item, index) => (
              <div key={index} className="cart-item">
                <h4>Custom PC {index + 1}</h4>
                <p><strong>CPU:</strong> {item.cpu.name} - ${item.cpu.price}</p>
                <p><strong>GPU:</strong> {item.gpu.name} - ${item.gpu.price}</p>
                <p><strong>Motherboard:</strong> {item.motherboard.name} - ${item.motherboard.price}</p>
                <p><strong>RAM:</strong> {item.ram.name} - ${item.ram.price}</p>
                <p><strong>Storage:</strong> {item.storage.name} - ${item.storage.price}</p>
                <p><strong>PSU:</strong> {item.psu.name} - ${item.psu.price}</p>
                <p><strong>Case:</strong> {item.case.name} - ${item.case.price}</p>
                <p><strong>Cooling:</strong> {item.cooling.name} - ${item.cooling.price}</p>
                <p><strong>Total Price:</strong> ${item.totalPrice}</p>
                <button onClick={() => removeFromCart(index)}>Remove</button>
              </div>
            ))}
          </div>

          <div className="cart-total">
            <h3>Total Price: ${totalPrice}</h3>
            <button onClick={clearCart}>Clear Cart</button>
            <Link to="/checkout">
              <button>Proceed to Checkout</button>
            </Link>
          </div>
        </>
      )}
    </div>
  );
};

export default Cart;
